import React from "react"
import "./index.css"
import num from "./NumArray"

class AddContent extends React.Component{
    constructor(){
        super()
        this.state = {
            items:[],
            currentItem: {
                task: "",
                id: ""
            }
        }
        this.handleInput = this.handleInput.bind(this);
        this.addItem = this.addItem.bind(this);
    }

    handleInput(e){
        this.setState({
            currentItem:{
                task: e.target.value,
                id: Date.now()
            }
        })
    }

    addItem(e){
        e.preventDefault()
        const newItem = this.state.currentItem;
        console.log(newItem)
    }

    render(){
        return(
            <div className="card">
                <form className="inline" >
                    <label className="inline">Item: 
                        <input type="text" name="name"className="buttonInp"
                        onChange={() => this.handleInput} />
                    </label>
                    <input type="submit" value="Add item" className="buttonAdd inline"
                     onClick={this.addItem} />
                </form>   
            </div>
        );
    }
}

export default AddContent;
